mapper.select:
			SELECT 
            snack_name
            , SUM(snack_quantity) AS 'snack_quantity'
            FROM BUY_DETAIL_SNACK
            WHERE payment_num = #{payment_num}
        	GROUP BY snack_name;

SELECT * FROM PAYMENTS p JOIN ORDERS o ON o.order_num = p.order_num
WHERE o.member_id LIKE '%${paymentSearchKeyword}%'
ORDER BY p.payment_datetime DESC LIMIT #{startRow} , #{listLimit}
